import { useState, useEffect } from "react";
import { Navbar, Container, Nav} from "react-bootstrap";
import logo from '../assets/img/logo.svg';
import logo2 from '../assets/img/logor.png';
import navIcon1 from '../assets/img/nav-icon1.svg';
import navIcon2 from '../assets/img/nav-icon2.svg';
import github from '../assets/img/github-icon.png';
import { HashLink } from 'react-router-hash-link';
import { ArrowRightCircle } from 'react-bootstrap-icons';
import cv from '/Users/rafaelabootcamp/dev/Portfolio-react/src/assets/img/Rafaela-Hollanda-Resume.pdf';
import {
  BrowserRouter as Router
} from "react-router-dom";

export const NavBar = () => {
    const [activeLink, setActiveLink] = useState('home');
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => { // this is to trigger the scrolling event listener
        const onScroll = () => {
            if (window.scrollY > 50) { // if the height is more than 50
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        }

        window.addEventListener('scroll', onScroll); 
        return () => window.removeEventListener('scroll', onScroll); // to remove when the component is removed from the DOM
    },[])

    const onUpdateActiveLink = (value) => {
        setActiveLink(value);
    }

    return (
        <Router>
            <Navbar expand="md" className={scrolled ? "scrolled" : ""}>
                <Container>
                    <Navbar.Brand href="#home">
                        <img src={logo2} alt="logo" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav">
                        <span className="navbar-toggler-icon"></span>
                    </Navbar.Toggle>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
                            <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
                            <Nav.Link href="#project" className={activeLink === 'project' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('project')}>Projects</Nav.Link>
                            {/* <Nav.Link href="#about" className={activeLink === 'about' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('about')}>About Me</Nav.Link> */}
                            <button onClick={() => console.log('connect')}><a href={cv} target='_blank' className="resume">Resume <ArrowRightCircle size={25}/></a></button>
                        </Nav>
                        <span className="navbar-text">
                            <div className="social-icon">
                                <a href="https://www.linkedin.com/in/rafaela-hollanda" target="_blank"><img src={navIcon1} alt=""/></a>
                                {/* <a href="#"><img src={navIcon2} alt=""/></a> */}
                                <a href="https://github.com/rphm95" target><img src={github} alt=""/></a>
                            </div>
                            <HashLink to='#connect'>
                                <button className="vvd" onClick={() => console.log('connect')}><span>Let's Connect</span></button>
                            </HashLink>
                        </span>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </Router>
    )
}

